<代码开始 lang="javascript">
// 函数方法化 将静态函数变换为方法
function methodize(fun, attr){
    return function(){
        var args = [].slice.call(arguments), self = attr?this[attr]:this;
        return fun.apply(null, [self].concat(args));
    }
}

// 数组循环静态函数
var forEach = function(arr,callback,pThis){
    for (var i =0,len=arr.length;i<len;i++){
        if (i in arr) callback.call(pThis,arr[i],i,arr);
    }
} 

//数组追加静态函数
var add = function(x,y){
	x.push(y);
	return x;
}

//方法化
Array.prototype.forEach = methodize(forEach);
Array.prototype.add = methodize(add);

//方法函数
Array.prototype.forEach2 = function(fn,idx){
    for (var i =0,len=this.length;i<len;i++){
        if (i in this) fn.call(idx,this[i],i,this);
    }
};

//测试
function print(el,index){ 
	console.log(index+" : "+el);
}


[1,2,3].forEach(print);
[1,2,3].forEach2(print);

console.log([1,2,3].add(2));
</代码结束>

